function keyByte() { return keyArray[(keyRead++)%keyNext]; }
function keyPressEntropy(e) { keyArray[(keyNext++)%256] ^= timeByte(); }

function mouseByte() { return mouseArray[(mouseRead++)%mouseNext]; }
